<?php/** * Created by PhpStorm. * User: Administrator * Date: 2018/4/7 0007 * Time: 11:01 */namespace app\rep\controller;use think\Controller;use think\Db;use think\Session;class Common extends Controller{    protected $rep_id;    protected $rep_agent_id;    protected $rep_name;    protected $rep_nickname;    protected $pageNum = 10;    public function __construct()    {        parent::__construct();        $this->rep_id = Session::get('rep_id');        if(intval($this->rep_id) == 0){            return $this->redirect(url('login/login'));        }        $rep_info = Db::name('rep')->where('rep_id',$this->rep_id)->field('rep_agent_id,rep_name,rep_username,rep_open_id')->find();        $this->rep_agent_id = session('rep_agent_id');        $this->rep_name = session('rep_name');        $this->rep_nickname = session('rep_nickname');        $this->assign('rep_id',$this->rep_id);        $this->assign('rep_info',$rep_info);    }}