<?php/** * Created by PhpStorm. * User: Administrator * Date: 2018/4/7 0007 * Time: 11:01 */namespace app\system\controller;use think\Controller;use think\Db;use think\Log;use think\Session;class Common extends Controller{    protected $adminId;    protected $adminNickname;    protected $pageNum = 10;    public function __construct()    {        parent::__construct();        $this->adminId = session('adminId');        $this->adminNickname = session('adminNickname');        if(intval($this->adminId) == 0){            return $this->redirect(url('login/login'));        }        $this->assign('adminId',$this->adminId);        $this->assign('adminNickname',$this->adminNickname);    }}