<?php/** * Created by PhpStorm. * User: Administrator * Date: 2018/4/7 0007 * Time: 11:01 */namespace app\system\controller;use think\Controller;use think\Db;use think\Session;class Common extends Controller{    protected $admin_id;    public function __construct()    {        parent::__construct();        $this->admin_id = session('admin_id');        if(intval($this->admin_id) == 0){            return $this->redirect(url('login/login'));        }        $admin = Db::name('admin')->where('admin_id',$this->admin_id)->find();        $this->assign('admin',$admin);    }}